mapper.select:
SELECT resource_info.RESOURCE_ID, resource_info.RESOURCE_NAME, resource_info.ISENABLE, ass_region_venue.*, ass_site_ticket.* FROM resource_info LEFT JOIN association_resource_showvenue_site ass_r_s_s ON resource_info.RESOURCE_ID = ass_r_s_s.RESOURCE_ID LEFT JOIN ( SELECT region_third.THIRD_ID, region_third.THIRD_NAME, showvenue_info.VENUE_ID, showvenue_info.VENUE_NAME FROM region_third_category region_third INNER JOIN showvenue_info ON region_third.THIRD_ID = showvenue_info.REGION_THIRD_ID
region_third.THIRD_ID = #{thirdId,jdbcType=DECIMAL}
AND showvenue_info.VENUE_ID = #{venueId,jdbcType=DECIMAL}
) ass_region_venue ON ass_r_s_s.VENUE_ID = ass_region_venue.VENUE_ID INNER JOIN ( SELECT site_info.SITE_ID, site_info.SITE_NAME, site_info.SITE_TIME, ticket_info.TICKET_ID, ticket_info.TICKET_PRICE,
IF ( ISNULL(ass_t_s.TICKET_COUNTS), 1, ass_t_s.TICKET_COUNTS ) AS TICKET_COUNTS FROM site_info LEFT JOIN association_site_ticket ass_s_t ON site_info.SITE_ID = ass_s_t.SITE_ID LEFT JOIN ticket_info ON ass_s_t.TICKET_ID = ticket_info.TICKET_ID LEFT JOIN association_ticket_suit ass_t_s ON ass_t_s.SUIT_TICKET_ID = ticket_info.TICKET_ID
ticket_info.TICKET_ID = #{ticketId ,jdbcType=DECIMAL}
) ass_site_ticket ON ass_r_s_s.SITE_ID = ass_site_ticket.SITE_ID
resource_info.RESOURCE_ID = #{resourceId ,jdbcType=DECIMAL}
AND ass_r_s_s.SITE_ID = #{siteId,jdbcType=DECIMAL}

mapper.update:
UPDATE ticket_info
SET TICKET_COUNTS = TICKET_COUNTS + #{counts,jdbcType=DECIMAL}
WHERE
	TICKET_ID = #{ticketId,jdbcType=DECIMAL}
