mapper.select:
SELECT
FROM user_base_info LEFT JOIN user_bind_info ON user_base_info.USER_ID = user_bind_info.USER_ID LEFT JOIN ( SELECT user_concerns.USER_ID, user_concerns.INTIME, user_concerns.CONCERN_ID, user_concerns.ISDEL,
resource_info.RESOURCE_ID, resource_info.RESOURCE_NAME, resource_info.RESOURCE_PIC, resource_info.ISENABLE, region_third.THIRD_ID, region_third.THIRD_NAME, showvenue_info.VENUE_ID, showvenue_info.VENUE_NAME, site_info.SITE_ID, site_info.SITE_NAME, site_info.SITE_TIME, ticket_info.TICKET_ID, ticket_info.TICKET_NAME FROM user_concerns LEFT JOIN resource_info ON user_concerns.RESOURCE_ID = resource_info.RESOURCE_ID LEFT JOIN showvenue_info ON showvenue_info.VENUE_ID = user_concerns.VENUE_ID LEFT JOIN region_third_category region_third ON region_third.THIRD_ID = user_concerns.REGION_ID LEFT JOIN site_info ON user_concerns.SITE_ID = site_info.SITE_ID LEFT JOIN ticket_info ON ticket_info.TICKET_ID = user_concerns.TICKET_ID ) user_concerns ON user_base_info.USER_ID = user_concerns.USER_ID LEFT JOIN receiveaddresses ON user_base_info.USER_ID = receiveaddresses.USER_ID LEFT JOIN ( SELECT ass_u_o.USER_ID, order_base_info.*, resource_info.RESOURCE_ID, resource_info.RESOURCE_NAME, resource_info.RESOURCE_PIC, resource_info.ISENABLE, region_third.THIRD_ID, region_third.THIRD_NAME, showvenue_info.VENUE_ID, showvenue_info.VENUE_NAME, site_info.SITE_ID, site_info.SITE_NAME, site_info.SITE_TIME, ass_o_r.TICKET_COUNTS, IF ( ISNULL(ass_t_s.TICKET_COUNTS), 1, ass_t_s.TICKET_COUNTS ) AS REAL_COUNTS FROM association_user_order ass_u_o LEFT JOIN order_base_info ON ass_u_o.ORDER_ID = order_base_info.ORDER_ID LEFT JOIN association_order_resource ass_o_r ON ass_o_r.ORDER_ID = order_base_info.ORDER_ID LEFT JOIN resource_info ON ass_o_r.RESOURCE_ID = resource_info.RESOURCE_ID LEFT JOIN showvenue_info ON showvenue_info.VENUE_ID = ass_o_r.VENUE_ID LEFT JOIN region_third_category region_third ON region_third.THIRD_ID = ass_o_r.REGION_THIRD_ID LEFT JOIN site_info ON ass_o_r.SITE_ID = site_info.SITE_ID LEFT JOIN ticket_info ON ticket_info.TICKET_ID = ass_o_r.TICKET_ID LEFT JOIN association_ticket_suit ass_t_s ON ass_t_s.SUIT_TICKET_ID = ticket_info.TICKET_ID
) ass_order_resource ON user_base_info.USER_ID = ass_order_resource.USER_ID
user_base_info.USER_ID = #{userId,jdbcType=DECIMAL}
